var flickrFeedApp = angular.module('flickrFeed', ['ngSanitize']);

flickrFeedApp.controller('FlickrFeedController', ['$scope', '$http', function ($scope, $http) {
    'use strict';

    $scope.listView = true;

    $scope.getJSONFlickrFeed = function () {
        var url = "https://api.flickr.com/services/feeds/photos_public.gne?callback=JSON_CALLBACK&tags=potato&tagmode=all&format=json#";
        $http.jsonp(url);
    };

    window.jsonFlickrFeed = function (data) {
        $scope.items = data.items;
    };

    $scope.openItem = function (item) {
        if (event.preventDefault) {
            event.preventDefault();
        } else {
            event.returnValue = false;
        }

        if ($scope.isItemOpen(item)) {
            $scope.openedItem = undefined;
        } else {
            $scope.openedItem = item;
        }
        $scope.listView = false;
    };

    $scope.isItemOpen = function (item) {
        return $scope.openedItem === item;
    };

    $scope.isAnyItemOpen = function (item) {
        return $scope.openedItem !== undefined;
    };

    $scope.closeItem = function () {
        if (event.preventDefault) {
            event.preventDefault();
        } else {
            event.returnValue = false;
        }
        $scope.openedItem = undefined;
        $scope.listView = true;
    };

    $scope.getJSONFlickrFeed();
}]);

flickrFeedApp.filter('dateSuffix', ['dateFilter', function (dateFilter) {
    'use strict';

    const suffixes = ["th", "st", "nd", "rd"];

    return function (date) {

        var filteredDate = dateFilter(date, "d MMM yyyy 'at' H:mm");
        var day = parseInt(filteredDate.slice(0, 2));
        var relevantDigits = day < 30 ? day % 20 : day % 30;
        var suffix = relevantDigits <= 3 ? suffixes[relevantDigits] : suffixes[0];

        // Insert date suffix into filteredDate and return
        var position = day.toString().length;
        return [filteredDate.slice(0, position), suffix, filteredDate.slice(position)].join('');
    };
}]);

flickrFeedApp.filter("regex", [function () {
    'use strict';

    return function (text, reg) {

        var result = text.match(new RegExp(reg));
        return result && result.length > 1 ? result[1] : '';
    };
}]);

flickrFeedApp.filter("split", [function () {
    'use strict';

    return function (input, splitChar) {

        return input.split(splitChar);
    };
}]);